import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import Todo from '../Todo/Todo';

function TodoList({ todoList, mode }) {
	const dispatch = useDispatch();
	const [completedLength, setCompletedLength] = useState(false);

	const handleRemoveAll = () => {
		dispatch({
			type: 'REMOVE_All_TODO',
			payload: [],
		});
	};

	useEffect(() => {
		todoList &&
			todoList.forEach((data) => {
				data.status === 'completed' && data && setCompletedLength(true);
			});
	}, [todoList]);

	return (
		<div>
			<div style={{ marginTop: '32px' }}>
				{todoList.map((data) => (
					<div key={data.id}>
						<Todo data={data} mode={mode} />
					</div>
				))}
			</div>

			{mode === 'completed' && completedLength && (
				<button onClick={handleRemoveAll}>RemoveAll</button>
			)}
		</div>
	);
}

export default TodoList;
