import React from 'react';
import { useDispatch } from 'react-redux';

function Todo({ data }) {
	const dispatch = useDispatch();
	const handleRemove = (id) => {
		console.log(id);
		dispatch({
			type: 'REMOVE_TODO',
			payload: id,
		});
	};

	return (
		<div>
			<p>{data.title}</p>
			<button onClick={() => handleRemove(data.id)}>remove</button>
		</div>
	);
}

export default Todo;
