import React from 'react';
import { useDispatch } from 'react-redux';
import './styles.scss';
function Todo({ data, id }) {
	const dispatch = useDispatch();

	const handleChange = (evt) => {
		id && id(evt.target.id);
		evt.target.checked &&
			dispatch({
				type: 'UPDATE_TODO',
				payload: {
					id: data.id,
					title: data.title,
					status: 'completed',
				},
			});

		evt.target.checked === false &&
			dispatch({
				type: 'UPDATE_TODO',
				payload: {
					id: data.id,
					title: data.title,
					status: 'active',
				},
			});
	};

	return (
		<div className='d-flex '>
			<input
				type='checkbox'
				defaultChecked={data.status === 'completed' && false}
				checked={data.status === 'completed' && true}
				onClick={handleChange}
				id={data.id}
			/>
			<p>{data.title}</p>
		</div>
	);
}

export default Todo;
