import React from 'react';
import { useDispatch, useSelector } from 'react-redux';

function Todo({ data }) {
	const dispatch = useDispatch();

	const handleRemove = (id) => {
		dispatch({
			type: 'REMOVE_TODO',
			payload: id,
		});
	};

	const handleChange = (evt) => {
		if (evt.target.checked) {
			dispatch({
				type: 'UPDATE_TODO',
				payload: {
					id: data.id,
					title: data.title,
					status: 'completed',
				},
			});
		}

		if (evt.target.checked === false) {
			dispatch({
				type: 'UPDATE_TODO',
				payload: {
					id: data.id,
					title: data.title,
					status: 'active',
				},
			});
		}
	};

	return (
		<div>
			<input type='checkbox' onClick={handleChange} id={data.id} />
			<p>{data.title}</p>
			<p>{data.status}</p>
			<button onClick={() => handleRemove(data.id)}>remove</button>
		</div>
	);
}

export default Todo;
