const initialState = {
	list: [
		{
			title: '',
			status: '',
		},
	],
	selectedId: null,
};

const todoReducer = (state = initialState, action) => {
	switch (action.type) {
		case 'ADD_TODO': {
			const newList = [...state.list];
			newList.push(action.payload);
			return {
				...state,
				list: newList,
			};
		}

		case 'REMOVE_TODO': {
			const removeTodo = action.payload;
			state = state.filter((todo) => todo.id !== removeTodo);
			return {
				...state,
				list: {},
			};
		}

		case 'UPDATE_TODO':
			return {
				...state,
				list: {
					...state.title,
					...action.status,
				},
			};

		default:
			return state;
	}
};
export default todoReducer;
