import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import './styles.scss';
function Todo({ data, id, mode }) {
	const [checked, setChecked] = useState(false);
	const dispatch = useDispatch();

	const handleChange = (evt) => {
		id && id(evt.target.id);
		setChecked(evt.target.checked);
		evt.target.checked &&
			dispatch({
				type: 'UPDATE_TODO',
				payload: {
					id: data.id,
					title: data.title,
					status: 'completed',
				},
			});

		evt.target.checked === false &&
			dispatch({
				type: 'UPDATE_TODO',
				payload: {
					id: data.id,
					title: data.title,
					status: 'active',
				},
			});
	};

	const handleRemove = (id) => {
		dispatch({
			type: 'REMOVE_TODO',
			payload: id,
		});
	};

	return (
		<div className='d-flex '>
			{mode === 'completed' && checked && (
				<>
					<p>{data.title}</p>
					<button onClick={() => handleRemove(data.id)}>
						remove
					</button>
				</>
			)}
			{mode === 'active' && !checked && (
				<>
					<input
						type='checkbox'
						defaultChecked={data.status === 'completed' && true}
						onClick={handleChange}
						id={data.id}
					/>
					<p>{data.title}</p>
				</>
			)}
			{mode === 'all' && (
				<>
					<input
						type='checkbox'
						defaultChecked={data.status === 'completed' && true}
						onClick={handleChange}
						id={data.id}
					/>
					<p>{data.title}</p>
				</>
			)}
		</div>
	);
}

export default Todo;
