import React from 'react';
import { useDispatch } from 'react-redux';

function Todo({ data }) {
	const dispatch = useDispatch();

	const handleRemove = (id) => {
		dispatch({
			type: 'REMOVE_TODO',
			payload: id,
		});
	};

	const handleChange = (id) => {
		// dispatch({
		// 	type: 'UPDATE_TODO',
		// 	payload: {
		// 		id,
		// 		title: data.title,
		// 		status:
		// 	}
		// });
	};

	return (
		<div>
			<input type='checkbox' onClick={handleChange} id={data.id} />
			<p>{data.title}</p>
			<button onClick={() => handleRemove(data.id)}>remove</button>
		</div>
	);
}

export default Todo;
