const initialState = {
	list: [
		{
			title: '',
			status: 'active',
			id: 0,
		},
	],
	selectedId: null,
};

const todoReducer = (state = initialState, action) => {
	switch (action.type) {
		case 'ADD_TODO': {
			const newList = [...state.list];
			newList.push(action.payload);
			return {
				...state,
				list: newList,
			};
		}

		case 'UPDATE_TODO': {
			let newList = [...state.list];
			state.list.map((item) => {
				newList.push(action.payload);
			});
			return {
				...state,
				list: newList,
			};
		}

		case 'REMOVE_TODO':
			return {
				...state,
				list: state.list.filter((item) => item.id !== action.payload),
			};

		default:
			return state;
	}
};
export default todoReducer;
