const initialState = {
	list: [
		{
			title: '',
			status: '',
		},
	],
	selectedId: null,
};

const todoReducer = (state = initialState, action) => {
	switch (action.type) {
		case 'ADD_TODO': {
			const newList = [...state.list];
			newList.push(action.payload);
			return {
				...state,
				list: newList,
			};
		}
		default:
			return state;
	}
};
export default todoReducer;
