import React from 'react';
import { useDispatch } from 'react-redux';

function Todo({ data }) {
	const dispatch = useDispatch();

	const handleChange = (evt) => {
		evt.target.checked &&
			dispatch({
				type: 'UPDATE_TODO',
				payload: {
					id: data.id,
					title: data.title,
					status: 'completed',
				},
			});

		evt.target.checked === false &&
			dispatch({
				type: 'UPDATE_TODO',
				payload: {
					id: data.id,
					title: data.title,
					status: 'active',
				},
			});
	};
	const handleRemove = (id) => {
		dispatch({
			type: 'REMOVE_TODO',
			payload: id,
		});
	};

	return (
		<div>
			<input
				type='checkbox'
				checked={true}
				onClick={handleChange}
				id={data.id}
			/>
			<p>{data.title}</p>
			<p>{data.status}</p>
			<button onClick={() => handleRemove(data.id)}>remove</button>
		</div>
	);
}

export default Todo;
